sql.query:
select o.id, o.description, 
(select a.ASSET_GROUP from  table(o.asset_details) a
) AS ASSET_GROUP,
(select a.ASSET_NUMBER from  table(o.asset_details) a
) AS ASSET_NUMBER,
(select a.PARENT_ASSET from  table(o.asset_details) a
) AS PARENT_ASSET,
(select a.ORGANIZATION_CODE from  table(o.asset_details) a
) AS ORGANIZATION_CODE,
(select a.WIP_CLASS from  table(o.asset_details) a
) AS WIP_CLASS,
(select t.ATTRIBUTE1 from  table(o.asset_attr_details) t
where t.ATTRIBUTE_GROUP='Provisioning Information'
) AS PROVISINING_TYPE,
(select t.ATTRIBUTE1 from  table(o.asset_attr_details) t
where t.ATTRIBUTE_GROUP='Customer Information'
) AS CUSTOMER_NAME,
(select t.ATTRIBUTE2 from  table(o.asset_attr_details) t
where t.ATTRIBUTE_GROUP='Customer Information'
) AS CUSTOMER_LOGIN,
(select t.ATTRIBUTE1 from  table(o.asset_attr_details) t
where t.ATTRIBUTE_GROUP='Service Locations'
) AS SERVICE_LOC1,
(select t.ATTRIBUTE2 from  table(o.asset_attr_details) t
where t.ATTRIBUTE_GROUP='Service Locations'
) AS SERVICE_LOC2,
(select t.ATTRIBUTE1 from  table(o.asset_attr_details) t
where t.ATTRIBUTE_GROUP='Account Information'
) AS ACCOUNT_INFO1,
(select t.ATTRIBUTE1 from  table(o.asset_attr_details) t
where t.ATTRIBUTE_GROUP='Account Information'
) AS ACCOUNT_INFO2,
(select t.ATTRIBUTE1 from  table(o.asset_attr_details) t
where t.ATTRIBUTE_GROUP='Contact Information'
) AS CONTACT_INFO1,
(select t.ATTRIBUTE2 from  table(o.asset_attr_details) t
where t.ATTRIBUTE_GROUP='Contact Information'
) AS CONTACT_INFO2
FROM xxdl_eam_ext_order o
